      "idareafuncional": $("#comunaOrdenAsignada").val(),
      "idcategoria":  $("#categoriaOrdenAsignada").val(),
      "idtipo": $("#tipoOrdenAsignada").val(),
      "idtecnico": $("#tÃ©cnicoOrdenAsignada").val()
