            tecnicoOrAs.push(p2.aaData[i].IDTECNICO + ',' + p2.aaData[i].NOMBRE);
            proyectoOrAs.push(p2.aaData[i].IDESTRUCTURA_OPERACION + ',' + p2.aaData[i].PROYECTO);
