function sanitizerSentence(sentence) {
  var sanitized = sentence.replace(/\s/g, '').normalize('NFD').replace(/[\u0300-\u036f]/g,'');
  return sanitized.charAt(0).toUpperCase() + sanitized.slice(1);
}

